mapper.select:
SELECT count(c.id) from a_resources c
where INSTR(c.path,
(SELECT CONCAT(',',p.code,',')  code from a_resources p
where p.app_id = #{appId, jdbcType=VARCHAR}
and p.id = #{id,jdbcType=BIGINT}
and p.is_delete = 0)
) > 0
and c.is_delete = 0

select count(r.id) from a_resources r
LEFT JOIN a_menu_group mg on r.menu_group_code = mg.`code`
where mg.app_id = #{appId, jdbcType=VARCHAR}
and mg.id = #{id,jdbcType=BIGINT}
and r.is_delete = 0
and mg.is_delete = 0

select DISTINCT
from a_resources r INNER join a_role_resource ra on r.id = ra.resource_id where 1=1
and r.menu_group_code = #{menuGroupCode, jdbcType=VARCHAR}
and r.type in
#{type}
and r.is_delete = 0 and r.is_enable = 1 and ra.role_id in ( SELECT DISTINCT r.id from a_role r INNER JOIN a_application_role ru on r.id = ru.role_id INNER JOIN a_applications a on ru.applications_id = a.id where 1=1 and a.app_id = #{appId, jdbcType=VARCHAR} and r.is_delete = 0 and r.is_enable = 1 and a.is_delete = 0 and a.is_enable = 1 )
